java.util.Map <Integer, TableColumn> tableColumnOrderMap= new java.util.TreeMap<Integer, TableColumn>();

boolean add = false;
String path;
Object obj = null;
int found_true = 0;
int found_false = 0;
int counter = 0;

//Merke urspr√ºngliche Reihenfolge der Columns

for (InitialTestCaseEntry itce: initialTestCaseEntry) {

   counter = 0;

	for (TableColumn tc: tableColumn) {


		try {
			path = tc.getPath();
		} catch(RuntimeException e) {
			throw new RuntimeException("Path value is not set!");
		}

		try {
			if (itce.get(path) != null) {
				obj = itce.get(path);
			}
			else {
				continue;
			}
		} catch(InvalidObjectPathException e) {
			throw new RuntimeException(e);
		}

		if (obj != null) {
			if (obj instanceof Integer && obj.equals(0)) {
				continue;
			}
			else if (obj instanceof Double && obj.equals(0d)) {
				continue;
			}
           else if (obj instanceof Boolean) {
               if ((Boolean) obj) {
                 found_true = 1;
               } else {
                 found_false = 1; 
               }
               // Object wird angezeigt, wenn es sowohl False als auch True Werte gibt
               if (found_true + found_false == 2) {
                 add = true;
               }
			}
			else if (obj instanceof Long && obj.equals(0L)) {
				continue;
			} else {
				add = true;
			}
		}
		if (add) {
			add = false;
			tableColumnOrderMap.put(counter, tc);
		}

   counter++;

	}
}

List < TableColumn > newTableColumn = new ArrayList < TableColumn >(tableColumnOrderMap.values());

return newTableColumn;

