List < InitialTestCaseEntry > tmp = new ArrayList < InitialTestCaseEntry > ();
String filter;
String path;
boolean add = true;
List < String > sort = new ArrayList < String > ();
base.Text text = new base.Text();

for (InitialTestCaseEntry to: initialTestCaseEntry) {


    for (TableColumn tc: tableColumn) {
        add = true;
        filter = tc.getFilter();
        path = tc.getPath();

        if (filter == null || filter == "") {
            add = true;
            continue;
        }
        try {
            if (String.valueOf(to.get(path)).contains(filter)) {
                add = true;
                continue;
            } else {
                add = false;
                break;
            }
        } catch (Exception e) {
            text.setText(e.toString());
        }
    }

    if (add) {
        tmp.add(to);
    }

}

initialTestCaseEntry = new ArrayList < InitialTestCaseEntry > (tmp);
