public static class Rethrower  {

  public static void rethrow(Throwable t) {
    Rethrower.<RuntimeException>rethrowHelper(t);
  }

  private static <T extends Throwable> void rethrowHelper(Throwable t) throws T {
    throw (T) t;
  }
