xmcp.oas.datatype.OASValidationResult result = new xmcp.oas.datatype.OASValidationResult();
result.setErrorMessages(new ArrayList<String>());
// validate ChangeList.changes of type String
try {
    var validator_changes = new com.gip.xyna.openapi.PrimitiveListTypeValidator<String,com.gip.xyna.openapi.StringTypeValidator>(com.gip.xyna.openapi.StringTypeValidator::new);
    validator_changes.setName("changes");
    validator_changes.setValue(getChanges());
    result.getErrorMessages().addAll(validator_changes.checkValid());
} catch (Exception e) {
    result.getErrorMessages().add("Error: could not validate ChangeList.changes");
}

return result.getResult();
