xmcp.oas.datatype.OASValidationResult result = new xmcp.oas.datatype.OASValidationResult();
result.setErrorMessages(new ArrayList<String>());
// validate ConstraintRuleDefinition.path of type String
try {
    var validator_path = new com.gip.xyna.openapi.PrimitiveListTypeValidator<String,com.gip.xyna.openapi.StringTypeValidator>(com.gip.xyna.openapi.StringTypeValidator::new);
    validator_path.setName("path");
    validator_path.setValue(getPath());
    result.getErrorMessages().addAll(validator_path.checkValid());
} catch (Exception e) {
    result.getErrorMessages().add("Error: could not validate ConstraintRuleDefinition.path");
}

// validate ConstraintRuleDefinition.expression of type String
try {
    var validator_expression = new com.gip.xyna.openapi.StringTypeValidator();
    validator_expression.setName("expression");
    validator_expression.setValue(getExpression());
    result.getErrorMessages().addAll(validator_expression.checkValid());
} catch (Exception e) {
    result.getErrorMessages().add("Error: could not validate ConstraintRuleDefinition.expression");
}

return result.getResult();
