xmcp.oas.datatype.OASValidationResult result = new xmcp.oas.datatype.OASValidationResult();
result.setErrorMessages(new ArrayList<String>());
// validate EventSubscription.id of type String
try {
    var validator_id = new com.gip.xyna.openapi.StringTypeValidator();
    validator_id.setName("id");
    validator_id.setValue(getId());
    validator_id.setRequired();
    result.getErrorMessages().addAll(validator_id.checkValid());
} catch (Exception e) {
    result.getErrorMessages().add("Error: could not validate EventSubscription.id");
}

// validate EventSubscription.callback of type String
try {
    var validator_callback = new com.gip.xyna.openapi.StringTypeValidator();
    validator_callback.setName("callback");
    validator_callback.setValue(getCallback());
    validator_callback.setRequired();
    result.getErrorMessages().addAll(validator_callback.checkValid());
} catch (Exception e) {
    result.getErrorMessages().add("Error: could not validate EventSubscription.callback");
}

// validate EventSubscription.query of type String
try {
    var validator_query = new com.gip.xyna.openapi.StringTypeValidator();
    validator_query.setName("query");
    validator_query.setValue(getQuery());
    result.getErrorMessages().addAll(validator_query.checkValid());
} catch (Exception e) {
    result.getErrorMessages().add("Error: could not validate EventSubscription.query");
}

return result.getResult();
