xmcp.oas.datatype.OASValidationResult result = new xmcp.oas.datatype.OASValidationResult();
result.setErrorMessages(new ArrayList<String>());
// validate TimePeriod.endDateTime of type String
try {
    var validator_endDateTime = new com.gip.xyna.openapi.StringTypeValidator();
    validator_endDateTime.setName("endDateTime");
    validator_endDateTime.setValue(getEndDateTime());
    validator_endDateTime.setFormat("date-time");
    result.getErrorMessages().addAll(validator_endDateTime.checkValid());
} catch (Exception e) {
    result.getErrorMessages().add("Error: could not validate TimePeriod.endDateTime");
}

// validate TimePeriod.startDateTime of type String
try {
    var validator_startDateTime = new com.gip.xyna.openapi.StringTypeValidator();
    validator_startDateTime.setName("startDateTime");
    validator_startDateTime.setValue(getStartDateTime());
    validator_startDateTime.setFormat("date-time");
    result.getErrorMessages().addAll(validator_startDateTime.checkValid());
} catch (Exception e) {
    result.getErrorMessages().add("Error: could not validate TimePeriod.startDateTime");
}

return result.getResult();
