xmcp.oas.datatype.OASValidationResult result = new xmcp.oas.datatype.OASValidationResult();
result.setErrorMessages(new ArrayList<String>());
// validate Response.body of type String
try {
    var validator_body = new com.gip.xyna.openapi.StringTypeValidator();
    validator_body.setName("body");
    validator_body.setValue(getBody());
    validator_body.setRequired();
    result.getErrorMessages().addAll(validator_body.checkValid());
} catch (Exception e) {
    result.getErrorMessages().add("Error: could not validate Response.body");
}

// validate Response.statusCode of type String
try {
    var validator_statusCode = new com.gip.xyna.openapi.StringTypeValidator();
    validator_statusCode.setName("statusCode");
    validator_statusCode.setValue(getStatusCode());
    result.getErrorMessages().addAll(validator_statusCode.checkValid());
} catch (Exception e) {
    result.getErrorMessages().add("Error: could not validate Response.statusCode");
}

// validate Response.header of type HeaderItem
try {
    List<? extends internalized.tmf640.datamodel.HeaderItem> header = getHeader();
    if (header != null) {
        if (header.size() < 1) {
          result.getErrorMessages().add("Response.header: List of type HeaderItem must have at least 1 items but has fewer");
        }
        for (internalized.tmf640.datamodel.HeaderItem obj : header) {
            result.getErrorMessages().addAll(obj.validate().getErrorMessages());
        }
    } else {
        result.getErrorMessages().add("Response.header: List of type HeaderItem is required but is null");
    }
} catch (Exception e) {
    result.getErrorMessages().add("Error: could not validate Response.header");
}

