List<String> pools = getConnectionPoolNames();
if (pools == null) {
  throw new IllegalArgumentException("No Connection Pools specified");  
}
java.util.Map<String, String> specifics = new java.util.HashMap<>();
StringBuilder sb = new StringBuilder();
boolean first = true;
for (String pool : pools) {
  if (first) {
    first = false;
  } else {
    sb.append(",");
  } 
  sb.append(pool);
}

specifics.put("connectionPools", sb.toString());

long timeout = getPoolWaitingTimeoutInMillis();
if (timeout != 0) {
  specifics.put("conTimeout", String.valueOf(timeout));
}

String clientInfo = getClientInfo();
if (clientInfo != null) {
  specifics.put("clientInfo", clientInfo);
}

//implemented in basetype
return openTx("ConnectionPoolTransactionType", specifics, correlatedXynaOrder);
